        static bool PointInPolygon(PointF p, PointF[] poly)
        {
            PointF p1, p2;

            bool inside = false;

            if (poly.Length < 3)
            {
                return inside;
            }
            PointF oldPoint = new PointF(
        poly[poly.Length - 1].X, poly[poly.Length - 1].Y);
            for (int i = 0; i < poly.Length; i++)
            {
                PointF newPoint = new PointF(poly[i].X, poly[i].Y);

                if (newPoint.X > oldPoint.X)
                {
                    p1 = oldPoint;
                    p2 = newPoint;

                }
                else
                {
                    p1 = newPoint;
                    p2 = oldPoint;

                }

                if ((newPoint.X < p.X) == (p.X <= oldPoint.X)

                    && (p.Y - p1.Y) * (p2.X - p1.X)

                     < (p2.Y - p1.Y) * (p.X - p1.X))
                {
                    inside = !inside;
                }
                oldPoint = newPoint;
            }
            return inside;
        }
        public ulke getCityAtPoint(System.Drawing.PointF p)
        {

            foreach (ulke c in Ulkeler)
            {
                if (PointInPolygon(p, c.ulke_Koordinat)) return c;
            }
            return null;
        }
